import React from "react";
import './index.js'
import './index.css'
// TODO: import useFormik from formik library
import {useFormik} from "formik";
function App() {
  // TODO: add a const called formik assigned to useFormik()
const formik = useFormik({
  initialValues: {
      email: '',
      password: ''
    },
    onSubmit: values => {
      console.log('form: ', values)
    },
    validate: values => {
      let errors = {}; 
      if (!values.email) errors.email = 'Field Required';
      if (!/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i.test(values.email))
     errors.email = 'Username should be an email';
      if (!values.password) errors.password = 'Field Required';
      else alert("Login Successful");
      return errors;
    }


});

  return (
    <div>
      <form onSubmit = {formik.handleSubmit}>
          <div>Email</div>
          <input name="emailField" type="text" onChange={formik.handleChange} value= {formik.values.email}/>
          {formik.errors.email ? <div name="emailError" style= {{color: 'red'}}>{formik.errors.email}</div>: null}
          <div>Password</div>
          <input name="pswField" type="text" onChange={formik.handleChange} value= {formik.values.password}/>
          {formik.errors.password ? <div name="pswError" style= {{color: 'red'}}>{formik.errors.password}</div>: null}  
          <button name="submitBtn" type="submit">Submit</button>    
      </form>
    </div>
  );
}

export default App;
